import axios from 'axios';

const apiUrl = "https://localhost:5244"; // ← חשוב מאוד! http ולא https

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/todos`);
    return result.data;
  },

  addTask: async (name) => {
    const newItem = { name, isComplete: false };
    const result = await axios.post(`${apiUrl}/todos`, newItem);
    return result.data;
  },

  setCompleted: async (id, isComplete) => {
    const updated = { id, isComplete, name: "" }; 
    // name חייב להיות שלם לפי השרת
    const result = await axios.put(`${apiUrl}/todos/${id}`, updated);
    return result.data;
  },

  deleteTask: async (id) => {
    await axios.delete(`${apiUrl}/todos/${id}`);
  }
};
